// src/App.js
import { useState, useEffect, useRef } from 'react';
import { MantineProvider, Card, Text, Button, TextInput, Paper, ActionIcon, Group } from '@mantine/core';
import { IconX, IconMessageCircle } from '@tabler/icons-react'; // Иконки для кнопок
import axios from 'axios';

// URL нашего бэкенда (не меняются)
const API_URL = 'http://localhost:8000/api/hello';
const WS_URL = 'ws://localhost:8000/ws';

// === Основной компонент виджета (сам чат) ===
function ChatWidget({ onClose }) {
  const [apiMessage, setApiMessage] = useState('Загрузка...');
  const [wsMessages, setWsMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const socket = useRef(null);

  // Загружаем данные по HTTP и подключаемся к WebSocket
  useEffect(() => {
    // Запрос по HTTP
    axios.get(API_URL)
      .then(response => setApiMessage(response.data.message))
      .catch(error => setApiMessage('Ошибка загрузки :('));

    // Подключение к WebSocket
    socket.current = new WebSocket(WS_URL);
    socket.current.onopen = () => console.log('WS соединение установлено');
    socket.current.onmessage = (event) => {
      setWsMessages(prev => [...prev, event.data]);
    };
    socket.current.onclose = () => console.log('WS соединение закрыто');

    // Чистка при закрытии виджета
    return () => {
      if (socket.current) {
        socket.current.close();
      }
    };
  }, []);

  const sendMessage = () => {
    if (inputValue && socket.current.readyState === WebSocket.OPEN) {
      socket.current.send(inputValue);
      setInputValue('');
    }
  };

  return (
    <Card shadow="xl" p="lg" radius="md" withBorder style={{ width: 350 }}>
      <Group position="apart" mb="md">
        <Text weight={500}>Чат-виджет</Text>
        <ActionIcon onClick={onClose}>
          <IconX size={16} />
        </ActionIcon>
      </Group>

      <Text size="sm" color="dimmed">
        Сообщение (HTTP): <b>{apiMessage}</b>
      </Text>

      <TextInput
        placeholder="Ваше сообщение..."
        value={inputValue}
        onChange={(event) => setInputValue(event.currentTarget.value)}
        mt="md"
      />
      <Button onClick={sendMessage} mt="sm" fullWidth>
        Отправить
      </Button>

      <Text size="sm" mt="lg">Ответы от WebSocket:</Text>
      <Paper withBorder p="xs" mt="xs" style={{ height: 120, overflowY: 'auto' }}>
        {wsMessages.length === 0 
          ? <Text size="xs" color="dimmed">Ждем сообщений...</Text>
          : wsMessages.map((msg, index) => <Text key={index} size="xs">{msg}</Text>)
        }
      </Paper>
    </Card>
  );
}


// === Компонент-обертка, который управляет состоянием ===
function App() {
  // Состояние для открытия/закрытия виджета
  const [isOpen, setIsOpen] = useState(false);

  // Стили для позиционирования в углу экрана
  const widgetContainerStyle = {
    position: 'fixed',
    bottom: '20px',
    right: '20px',
    zIndex: 1000, // Чтобы было поверх всего
  };

  return (
    <div style={widgetContainerStyle}>
      {isOpen ? (
        // Если открыто - показываем сам виджет
        <ChatWidget onClose={() => setIsOpen(false)} />
      ) : (
        // Если закрыто - показываем кнопку для открытия
        <Button
          onClick={() => setIsOpen(true)}
          radius="xl"
          size="lg"
          shadow="xl"
          leftIcon={<IconMessageCircle size={24} />}
        >
          Click ME
        </Button>
      )}
    </div>
  );
}

// Оборачиваем App в MantineProvider, как и раньше
export default function MantineApp() {
  return (
    <MantineProvider withGlobalStyles withNormalizeCSS>
      <App />
    </MantineProvider>
  );
}
